import React, { useState } from 'react';
import API from '../api';

export default function SearchBook() {
  const [query, setQuery] = useState({ capacityRequired: '', fromPincode: '', toPincode: '', startTime: '' });
  const [results, setResults] = useState([]);
  const [message, setMessage] = useState('');

  const handleChange = e => setQuery({ ...query, [e.target.name]: e.target.value });

  const handleSearch = async () => {
    try {
      const res = await API.get('/vehicles/available', { params: query });
      setResults(res.data);
      setMessage('');
    } catch (err) {
      setMessage('Error: ' + err.response?.data?.error);
    }
  };

  const handleBook = async (vehicleId) => {
    try {
      const payload = { ...query, vehicleId, customerId: 'cust123' };
      const res = await API.post('/bookings', payload);
      setMessage('Booking confirmed: ' + res.data._id);
    } catch (err) {
      setMessage('Error: ' + err.response?.data?.error);
    }
  };

  return (
    <div>
      <h2>Search & Book</h2>
      <input name="capacityRequired" placeholder="Capacity Required" value={query.capacityRequired} onChange={handleChange} />
      <input name="fromPincode" placeholder="From Pincode" value={query.fromPincode} onChange={handleChange} />
      <input name="toPincode" placeholder="To Pincode" value={query.toPincode} onChange={handleChange} />
      <input name="startTime" type="datetime-local" value={query.startTime} onChange={handleChange} />
      <button onClick={handleSearch}>Search</button>

      {results.map(r => (
        <div key={r._id}>
          <p>{r.name} - {r.capacityKg}kg - {r.tyres} tyres - {r.estimatedRideDurationHours}h</p>
          <button onClick={() => handleBook(r._id)}>Book Now</button>
        </div>
      ))}

      <p>{message}</p>
    </div>
  );
}
